window.addEventListener("load",(()=>{const e=document.querySelector("#New-Task"),t=document.querySelector("#add_new_task"),n=document.querySelector("#tasks");e.addEventListener("submit",(e=>{e.preventDefault();const d=t.value;if(!d)return void alert("What do you want to do?");console.log("woo!");const a=document.createElement("div");a.classList.add("task");const o=document.createElement("div");o.classList.add("content"),a.appendChild(o);const s=document.createElement("input");s.classList.add("text"),s.type="text",s.value=d,s.setAttribute("readonly","readonly"),o.appendChild(s);const c=document.createElement("div");c.classList.add("actions");const i=document.createElement("button");i.classList.add("Edit"),i.innerHTML="Edit";const l=document.createElement("button");l.classList.add("Delete"),l.innerHTML="Delete",c.appendChild(i),c.appendChild(l),a.appendChild(c),n.appendChild(a),t.value="",i.addEventListener("click",(()=>{"edit"==i.innerText.toLowerCase()?(s.removeAttribute("readonly"),s.focus(),i.innerText="Save"):(s.setAttribute("readonly","readonly"),i.innerText="Edit")})),l.addEventListener("click",(()=>n.removeChild(a)))}))}));